(()=>{"use strict";document.querySelector(".generator").addEventListener("submit",(e=>{e.preventDefault();const t=document.querySelector('[name = "users-number"]').value,r=document.querySelector('[name = "gender"]').value;fetch(`https://randomuser.me/api/?results=${t}&gender=${"both"===r?"male,female":r}`).then((e=>{if(200!==e.status)throw Error("To nie jest odpowedÅº 200");return e.json()})).then((e=>(e=>{const t=document.querySelector(".user-list");t.textContent="",e.forEach((e=>{const r=document.createElement("div");r.className="user",r.innerHTML=`\n    <div class="user__name">${e.name.title.toUpperCase()} ${e.name.first.toUpperCase()} ${e.name.last.toUpperCase()}</div>\n    <img class="user__image" src=${e.picture.medium}>`,t.appendChild(r)}))})(e.results))).catch((e=>console.log(e)))}))})();